package Mukesh_conditionalStatement;

public class if_elseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name = "tanzir";
		if (name.equalsIgnoreCase("anika")) {
			System.out.println("print aniak");
		}

		else if (name.equalsIgnoreCase("ashfia")) {
			System.out.println("ashfia");
		}

		else {
			System.out.println("tanzir");
		}

	}

}
